/*Table structure for table `worktab` */

DROP TABLE IF EXISTS `worktab`;

CREATE TABLE `worktab` (
  `id` int(10) unsigned NOT NULL auto_increment COMMENT '主键',
  `factnum` int(100) default NULL COMMENT '实到总数',
  `shouldnum` int(100) default NULL COMMENT '应到总数',
  `dutyhead` varchar(200) default NULL COMMENT '值班首长',
  `dutyer` varchar(200) default NULL COMMENT '值班员',
  `gbfactnum` int(100) default NULL COMMENT '干部实到人数',
  `gbshouldnum` int(100) default NULL COMMENT '干部应到人数',
  `gbholiday` int(100) default NULL COMMENT '干部休假',
  `gbout` int(100) default NULL COMMENT '干部外出',
  `sgfactnum` int(100) default NULL COMMENT '士官实在人数',
  `sgshouldnum` int(100) default NULL COMMENT '士官应该人数',
  `sgholiday` int(100) default NULL COMMENT '士官休假',
  `sgout` int(100) default NULL COMMENT '士官外出',
  `ywbfactnum` int(100) default NULL COMMENT '义务兵实在人数',
  `ywbshouldnum` int(100) default NULL COMMENT '义务兵应在人数',
  `ywbholiday` int(100) default NULL COMMENT '义务兵休假',
  `ywbout` int(100) default NULL COMMENT '义务兵外出',
  `remark` varchar(1000) default NULL COMMENT '备注',
  `yflag` int(5) default NULL COMMENT '营级审核标志位  0：未审核， 1：审核',
  `tflag` int(5) default NULL COMMENT '团级审核标志位  0：未审核， 1：审核',
  `submitflag` int(5) default NULL COMMENT '提交标志位  0：未， 1：已',
  `userid` int(10) default NULL COMMENT '用户id',
  `inputtime` datetime default NULL COMMENT '录入时间',
  `weather` varchar(1000) default NULL COMMENT '天气',
  `Healthprevention` varchar(1000) default NULL COMMENT '卫生防疫',
  `onedaywork` varchar(2000) default NULL COMMENT '一日工作开展情况',
  `headdownlian` varchar(2000) default NULL COMMENT '首长机关下连情况',
  PRIMARY KEY  (`id`)
